	}
	iterator insert(const_iterator _Where, const _Ty& _Val)
	{	// insert _Val at _Where
		size_type _Off = size() == 0 ? 0 : _Where - begin();
		_Insert_n(_Where, (size_type)1, _Val);
		return (begin() + _Off);
	}
